import { View, Text } from 'react-native';
import styles from '../styles.js'
import { useRoute } from '@react-navigation/native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import { FontAwesome } from '@expo/vector-icons';
import { useState } from 'react';

const HomeScreen = ({navigation}) => {
    const route = useRoute();
    const { username } = route.params;
    const [groups, setGroups] = useState({
        "Group 1":["amigo1", "amigo2"],
        "Group 2":["amigo3", "amigo4"],
        "Group 3":["amigo5", "amigo6"]}
    );

    const addGroup = (newGroup) => {
        console.log(groups);
        console.log(newGroup);
        setGroups([...groups, newGroup]);
    };

    return (
        <View style={styles.containerHome}>
            <View style={styles.header}>
                <View style={styles.profile}>
                    <FontAwesome name="user-circle" size={32} color="gray" style={styles.profileIcon}/>
                    <Text style={styles.username}>Hello, {username}</Text>
                </View>
            </View>

            <View style={styles.groupList}>
                {Object.entries(groups).map((groupName, members) => (
                    <TouchableOpacity
                        key={index}
                        style={styles.groupButton}
                        onPress={() => navigation.navigate('GroupDetails', { groupName: groupName, members: members})}
                    >
                        <Text style={styles.groupButtonText}>{groupName}</Text>
                    </TouchableOpacity>
                ))}
            </View>

            <TouchableOpacity style={styles.createGroupButton} onPress={() => navigation.navigate('CreateGroup', {addGroup})}>
                <Text style={styles.createGroupButtonText}>Criar Grupo</Text>
            </TouchableOpacity>
        </View>
    );
};

export default HomeScreen;
