import { View, Text } from 'react-native';
import styles from '../styles.js'
import { useNavigation, useRoute } from '@react-navigation/native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import { FontAwesome } from '@expo/vector-icons';
import { useState } from 'react';

const HomeScreen = () => {
    const navigation = useNavigation()
    const route = useRoute();
    const { username } = route.params;
    const [groups, setGroups] = useState(["Group 1", "Group 2", "Group 3"]);

    const addGroup = (newGroup) => {
        setGroups([...groups, newGroup]);
    }

    return (
        <View style={styles.containerHome}>
            <View style={styles.header}>
                <View style={styles.profile}>
                    <FontAwesome name="user-circle" size={32} color="gray" style={styles.profileIcon}/>
                    <Text style={styles.username}>Hello, {username}</Text>
                </View>
            </View>

            <View style={styles.groupList}>
                {groups.map((group, index) => (
                    <TouchableOpacity
                        key={index}
                        style={styles.groupButton}
                        onPress={() => navigation.navigate('GroupDetails', { groupName: group})}
                    >
                        <Text style={styles.groupButtonText}>{group}</Text>
                    </TouchableOpacity>
                ))}
            </View>

            <TouchableOpacity style={styles.createGroupButton} onPress={() => navigation.navigate('CreateGroupScreen', {addGroup})}>
                <Text style={styles.createGroupButtonText}>Criar Grupo</Text>
            </TouchableOpacity>
        </View>
    );
};

export default HomeScreen;
