import { useState } from "react";
import { useRoute } from '@react-navigation/native';
import { View, Text, TextInput, Button } from 'react-native';
import { TouchableOpacity } from "react-native-web";
import { Fontisto } from '@expo/vector-icons';
import { Ionicons } from '@expo/vector-icons';
import styles from '../styles.js'

const CreateGroupScreen = ({navigation}) => {
    const route = useRoute();
    const { addGroup, username } = route.params;
    const [groupName, setGroupName] = useState('');
    const [members, setMembers] = useState([]);
    const [newMember, setNewMember] = useState({name:'', value:''});

    const handleSubmit = () => {
        const newGroup = {
            groupName,
            members: [...members, { name: username, value: 0}], // Adicione o usuÃ¡rio atual no final da lista
        };
        if (groupName === '') {
            alert('Nome do grupo invalido');
            return;
        }

        addGroup(newGroup);

        navigation.goBack();
    };

    const addMember = () => {
        if (newMember.name.trim() !== '' && newMember.value.trim() !== '') {
            setMembers([...members, newMember]);
            setNewMember({name:'', value:''});
        }
    };

    const removeMember = (index) => {
        const updatedMembers = [...members];
        updatedMembers.splice(index, 1);
        setMembers(updatedMembers);
    };

    return (
        <View style={styles.containerHome}>
            <Text>Criar um novo grupo</Text>
            <TextInput
                placeholder="Nome do Grupo"
                value={groupName}
                onChangeText={(text) => setGroupName(text)}
                style={styles.inputCreateGroup}
            />

            <Text>Integrantes:</Text>
            <View style={styles.membersList}>
                {members.map((member, index) => (
                    <View key={index} style={styles.memberItem}>
                        <Text>{member.name} - Valor: {member.value}</Text>
                        <TouchableOpacity onPress={() => removeMember(index)}>
                            <Fontisto name="trash" size={24} color="black" style={styles.removeMemberButton}/>
                        </TouchableOpacity>
                    </View>
                ))}
            </View>

            <View style={styles.addMemberContainer}>
                <TextInput
                    placeholder="Nome"
                    value={newMember.name}
                    onChangeText={(text) => setNewMember({...newMember, name:text})}
                    style={styles.inputCreateGroup}
                />
                <TextInput
                    placeholder="Valor"
                    value={newMember.value}
                    onChangeText={(text) => setNewMember({...newMember, value:text})}
                    style={styles.inputCreateGroup}
                />
                <TouchableOpacity onPress={addMember}>
                    <Ionicons name="add-circle-outline" size={24} color="black" style={styles.addMemberButton}/>
                </TouchableOpacity>
            </View>

            <Button title="Criar grupo" onPress={handleSubmit}/>
        </View>
    );
};

export default CreateGroupScreen;
